func rotate(nums string) {
	// Split the string by spaces
	strNumbers := strings.Split(nums, ",")
	// Create a slice to store integers
	var degrees []int
	// Convert each string to int
	for _, str := range strNumbers {
		num, err := strconv.Atoi(str)
		if err != nil {
			fmt.Printf("Error converting %s: %v\n", str, err)
			continue
		}
		degrees = append(degrees, num)
	}
	fmt.Println(degrees)
	// Calculate final turn
	clockwise := 0
	counterclockwise := 0
	finalPosition := 0
	for _, n := range degrees {
		if n < 0 {
			move := 360 + (n)
			counterclockwise = counterclockwise + move
			continue
		}
		clockwise = clockwise + n
	}
	finalPosition = (clockwise + counterclockwise) % 360
	fmt.Printf("Contra relógio: %d\n", counterclockwise)
	fmt.Printf("Relógio: %d\n", clockwise)
	fmt.Printf("Soma das variáveis: %d\n", clockwise+counterclockwise)
	fmt.Printf("Posição final: %d\n", finalPosition)
}
